import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVirus } from '@fortawesome/free-solid-svg-icons';


class Cell extends React.Component {
  constructor(props) {
    super();

    this.bgColors = ['#f4f4f4', '#D7F7CA', '#DCDA8F', '#EEC58F', '#FFA166', '#FF7566', '#FF5166'];

    this.state = {
      backgroundColor: props.cellOptions.mined && props.cellOptions.opened ? '#666' :
        props.cellOptions.opened ? this.bgColors[this.props.cellOptions.minesAround] : '#BABDB6'
    }
  }

  handleClick = (event) => {
    if (this.state.opened) {
      return;
    } 

    // console.log(this.props.cellOptions.id);
    this.props.handleClick(this.props.cellOptions.id);
  }

  render() {

    const fz = this.props.styleOptions.width < 40 ? '20px' : this.props.styleOptions.width < 50 ? '30px' : '50px';

    const cellStyle = {
      ...this.props.styleOptions,
      backgroundColor: props.cellOptions.mined && props.cellOptions.opened ? '#666' :
      props.cellOptions.opened ? this.bgColors[this.props.cellOptions.minesAround] : '#BABDB6'
    };

    const textContent = 
    this.props.cellOptions.minesAround > 0 ?
    this.props.cellOptions.minesAround :
    this.props.cellOptions.mined ? 
    (<FontAwesomeIcon icon={faVirus} style={{fontSize: `${this.props.styleOptions.width}`, textAlign: 'center', width: '100%'}}/>) :
    '';

    return (
      <div className='cell' style={cellStyle} onClick={this.handleClick}>
        <div className='cell-content' style={{fontSize: `${fz}`}}>
          {this.props.cellOptions.opened ? textContent : null}
        </div>
      </div>
    );
  }
}

export default Cell;
