import React from 'react';

class Ceil extends React.Component {
  constructor(props) {
    super();
  }

  render() {

    const style = {
      ...this.props.styleOptions,
      backgroundColor: this.props.ceilOptions.mined ? 'red' : ''
    };

    const fz = this.props.ceilSize < 40 ? '14px' : this.props.ceilSize < 50 ? '18px' : '30px';

    // console.log(this.props)
    return (
      <div className='ceil' style={style}>
        <div className='ceil-content' style={{fontSize: `${fz}`}}>
          {this.props.ceilOptions.minesAround ?? ''}
        </div>
      </div>
    );
  }
}

export default Ceil;
