import React from 'react';
import Board from './board';
import AsideMenu from './aside-menu';

class Game extends React.Component {
  constructor(props) {
    super();
  }

  state = {
    boardWidth: 0,
    ceils: {},
    mines: {}
  }

  componentDidMount() {
    if (this.state.boardWidth === 0) {
      this.gameInit();
    }
  }

  gameInit = () => {
    const sizes = this.getBoardAndCeilSize();
    const newBoardWidth = sizes.boardWidth;
    const minesObject = this.getRandomMines();
    const ceilsObject = this.getCeils(minesObject);


    for (let ceil of Object.keys(ceilsObject)) {
        if (!ceilsObject[ceil].mined) {

          const newMinesAround = this.getMinesAround(ceilsObject, ceil, minesObject);
          ceilsObject[ceil].minesAround = newMinesAround;
        }      
    }

    this.setState({
      boardWidth: newBoardWidth,
      ceilSize: sizes.ceilSize,
      ceils: ceilsObject,
      mines: minesObject
    });
  }

  getBoardAndCeilSize() {
    let newCeilSize = 90;

    if (this.props.gameDifficult.width === '16') {
      newCeilSize = 45;
    }  else if (this.props.gameDifficult.width === '30') {
      newCeilSize = 35;
    }

    return {
      ceilSize: newCeilSize,
      boardWidth: +this.props.gameDifficult.width * newCeilSize + (this.props.gameDifficult.width * 2)
    }    
  }

  getRandomMines() {

    let mines = 0;

    if (this.props.gameDifficult.width === '8') {
      mines = 10;
    } else if (this.props.gameDifficult.width === '16') {
      mines = 40;
    } else if (this.props.gameDifficult.width === '30') {
      mines = 99;
    }

    let minesObject = {};

    for (let i = 1; i <= mines; i++) {
      let randomCol = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
      let randomRow = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      while (minesObject[randomCol + '/' + randomRow]) {
        randomCol = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
        randomRow = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      };
      minesObject[randomCol + '/' + randomRow] = true;
    }

    return minesObject;
  }

  getCeils(minesObject) {

    let ceilsObject = {};

    for (let row = 1; row <= this.props.gameDifficult.height; row++) {
      for (let col = 1; col <= this.props.gameDifficult.width; col++) {
        let isMined = minesObject[col + '/' + row] ? true : false;

        ceilsObject[col + '/' + row] = {
          id: col + '/' + row,
          row: row,
          col: col,
          mined: isMined,
          opened: false,
          flagged: false,
          minesAround: 0
        };
      }
    }

    return ceilsObject;
  }

  getMinesAround(ceilsObject, ceil, minesObject) {
    let row = ceilsObject[ceil].row;
    let col = ceilsObject[ceil].col;

    let arroundCeils = [
      (col + 1) + '/' + (row),
      (col - 1) + '/' + (row),
      (col) + '/' + (row + 1),
      (col) + '/' + (row - 1),
      (col + 1) + '/' + (row + 1),
      (col - 1) + '/' + (row - 1),
      (col + 1) + '/' + (row - 1),
      (col - 1) + '/' + (row + 1)
    ];

    let newMinesAround = 0;

    arroundCeils = arroundCeils.forEach(item => {

      let reg = new RegExp(
        `^(0\/)|(\/0)|(${+this.props.gameDifficult.width + 1}\/)|(\/${+this.props.gameDifficult.height + 1})`,
        'g'
      );

      if (!item.match(reg)) {
        newMinesAround = minesObject[item] ?  newMinesAround += 1 : newMinesAround;
      } else {
        newMinesAround = newMinesAround;
      }
    });

    return newMinesAround;
  }

  render() {
    return (
      <div className="container">
        <Board gameState={this.state}/>
        <AsideMenu handleGameStatus={this.props.handleGameStatus} />
      </div>
    );
  }
}

export default Game;
