import React from 'react';
import Board from './board';
import AsideMenu from './aside-menu';

class Game extends React.Component {
  constructor(props) {
    super();
  }

  state = {
    boardWidth: 0,
    cells: {},
    mines: {}
  }

  componentDidMount() {
    if (this.state.boardWidth === 0) {
      this.gameInit();
    }
  }

  handleClick = (id) => {

    let currentCell = this.state.cells[id];

    console.log(currentCell);

  }

  gameInit = () => {
    const sizes = this.getBoardAndCellSize();
    const newBoardWidth = sizes.boardWidth;
    const minesObject = this.getRandomMines();
    const cellsObject = this.getCells(minesObject);


    for (let cell of Object.keys(cellsObject)) {
        if (!cellsObject[cell].mined) {

          const newMinesAround = this.getMinesAround(cellsObject, cell, minesObject);
          cellsObject[cell].minesAround = newMinesAround;
        }      
    }

    this.setState({
      boardWidth: newBoardWidth,
      cellSize: sizes.cellSize,
      cells: cellsObject,
      mines: minesObject
    });
  }

  getBoardAndCellSize() {
    let newCellSize = 90;

    if (this.props.gameDifficult.width === '16') {
      newCellSize = 45;
    }  else if (this.props.gameDifficult.width === '30') {
      newCellSize = 35;
    }

    return {
      cellSize: newCellSize,
      boardWidth: +this.props.gameDifficult.width * newCellSize + (this.props.gameDifficult.width * 2)
    }    
  }

  getRandomMines() {

    let mines = 0;

    if (this.props.gameDifficult.width === '8') {
      mines = 10;
    } else if (this.props.gameDifficult.width === '16') {
      mines = 40;
    } else if (this.props.gameDifficult.width === '30') {
      mines = 99;
    }

    let minesObject = {};

    for (let i = 1; i <= mines; i++) {
      let randomCol = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
      let randomRow = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      while (minesObject[randomCol + '/' + randomRow]) {
        randomCol = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
        randomRow = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      };
      minesObject[randomCol + '/' + randomRow] = true;
    }

    return minesObject;
  }

  getCells(minesObject) {

    let cellsObject = {};

    for (let row = 1; row <= this.props.gameDifficult.height; row++) {
      for (let col = 1; col <= this.props.gameDifficult.width; col++) {
        let isMined = minesObject[col + '/' + row] ? true : false;

        cellsObject[col + '/' + row] = {
          id: col + '/' + row,
          row: row,
          col: col,
          mined: isMined,
          opened: false,
          flagged: false,
          minesAround: 0
        };
      }
    }

    return cellsObject;
  }

  getMinesAround(cellsObject, cell, minesObject) {
    let row = cellsObject[cell].row;
    let col = cellsObject[cell].col;

    let arroundCells = [
      (col + 1) + '/' + (row),
      (col - 1) + '/' + (row),
      (col) + '/' + (row + 1),
      (col) + '/' + (row - 1),
      (col + 1) + '/' + (row + 1),
      (col - 1) + '/' + (row - 1),
      (col + 1) + '/' + (row - 1),
      (col - 1) + '/' + (row + 1)
    ];

    let newMinesAround = 0;

    arroundCells = arroundCells.forEach(item => {

      let reg = new RegExp(
        `^(0\/)|(\/0)|(${+this.props.gameDifficult.width + 1}\/)|(\/${+this.props.gameDifficult.height + 1})`,
        'g'
      );

      if (!item.match(reg)) {
        newMinesAround = minesObject[item] ?  newMinesAround += 1 : newMinesAround;
      } else {
        newMinesAround = newMinesAround;
      }
    });

    return newMinesAround;
  }

  render() {
    return (
      <div className="container">
        <Board gameState={this.state} handleClick={this.handleClick}/>
        <AsideMenu handleGameStatus={this.props.handleGameStatus} />
      </div>
    );
  }
}

export default Game;
