import React from 'react';
import Ceil from './ceil';

class Board extends React.Component {
  constructor(props) {
    super();
  }

  state = {
    ceils: {},
    boardWidth: 0,
    ceilSize: 0
  }

  componentDidMount() {
    if (this.state.boardWidth === 0) {
      this.gameInit();
    }
  }

  gameInit = () => {

    let newCeilSize = 90;

    if (this.props.gameDifficult.width === '16') {
      newCeilSize = 45;
    }  else if (this.props.gameDifficult.width === '30') {
      newCeilSize = 35;
    }

    let newBoardWidth = +this.props.gameDifficult.width * newCeilSize + (this.props.gameDifficult.width * 2);

    let mines = 0;
    if (this.props.gameDifficult.width === '8') {
      mines = 10;
    } else if (this.props.gameDifficult.width === '16') {
      mines = 40;
    } else if (this.props.gameDifficult.width === '30') {
      mines = 99;
    }

    let minesArray = [];

    for (let i = 1; i <= mines; i++) {
      let randomWidth = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
      let randomHeight = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      minesArray.push([randomWidth, randomHeight]);
    }

    let ceilsObject = {};

    for (let i = 1; i <= this.props.gameDifficult.height; i++) {
      for (let j = 1; j <= this.props.gameDifficult.width; j++) {

        ceilsObject[j + '/' + i] = {
          cords: [j, i],
          mined: false,
          minesArround: null,
          opened: false
        };
      }
    }

    minesArray.forEach((mine) => {
      ceilsObject[mine[0] + '/' + mine[1]].mined = true;      
    });

    // for (let key of Object.keys(ceilsObject)) {
    //   if (!ceilsObject[key].mined) {
    //     let col = ceilsObject[key].cords[0];
    //     let row = ceilsObject[key].cords[1];
    //   }
    // }

    this.setState({
      ceils: ceilsObject,
      boardWidth: newBoardWidth,
      ceilSize: newCeilSize
    })
  }

  getCeil = () => {
    let ceilArray = [];
    let f = 0;
    for (let key of Object.keys(this.state.ceils)) {
      f = this.state.ceils[key].mined ? f + 1 : f;
      ceilArray.push(
        <Ceil 
          key={key}
          ceilCords={key}
          ceilOptions={this.state.ceils[key]}
          styleOptions={{width: this.state.ceilSize, height: this.state.ceilSize}}
        />
      );
    }

    console.log(f);

    return ceilArray;
  }

  render () {

    // console.log(this.state);
    return (
      <div className='board' style={{width: `${this.state.boardWidth}px`}}>

        {this.state.boardWidth ? this.getCeil() : null}

      </div>
    );
  }
}

export default Board;
