  constructor(props) {
    super();
  }

  state = {
    boardWidth: 0,
    ceilSize: 0,
    ceils: {},
    mines: {}
  }

  componentDidMount() {
    if (this.state.boardWidth === 0) {
      this.gameInit();
    }
  }

  gameInit() {
    const newBoardWidth = this.getBoardSize();
    const minesObject = this.getMines();

    console.log('mines: ', Object.keys(minesObject).length);

  }

  getBoardSize() {
    let ceilSize = 90;

    if (this.props.gameDifficult.width === '16') {
      ceilSize = 45;
    }  else if (this.props.gameDifficult.width === '30') {
      ceilSize = 35;
    }

    return +this.props.gameDifficult.width * ceilSize + (this.props.gameDifficult.width * 2);    
  }

  getMines() {

    let mines = 0;

    if (this.props.gameDifficult.width === '8') {
      mines = 10;
    } else if (this.props.gameDifficult.width === '16') {
      mines = 40;
    } else if (this.props.gameDifficult.width === '30') {
      mines = 99;
    }

    let minesObject = {};

    for (let i = 1; i <= mines; i++) {
      let randomCol = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
      let randomRow = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      while (minesObject[randomCol + '/' + randomRow]) {
        randomCol = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
        randomRow = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      };
      minesObject[randomCol + '/' + randomRow] = true;
    }

    return minesObject;
  }

