import React from 'react';
import Board from './board';
import AsideMenu from './aside-menu';

class Game extends React.Component {
  constructor(props) {
    super();
  }

  state = {
    boardWidth: 0,
    ceilSize: 0,
    ceils: {},
    mines: {}
  }

  componentDidMount() {
    if (this.state.boardWidth === 0) {
      this.gameInit();
    }
  }

  gameInit() {
    const newBoardWidth = getBoardSize();


  }

  getBoardSize() {
    let ceilSize = 90;

    if (this.props.gameDifficult.width === '16') {
      ceilSize = 45;
    }  else if (this.props.gameDifficult.width === '30') {
      ceilSize = 35;
    }

    return +this.props.gameDifficult.width * ceilSize + (this.props.gameDifficult.width * 2);    
  }

  render() {

    return (
      <div className="container">
        <Board gameDifficult={this.props.gameDifficult}/>
        <AsideMenu handleGameStatus={this.props.handleGameStatus}/>
      </div>
    );
  }
}

export default Game;
