import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock } from '@fortawesome/free-regular-svg-icons';
import { faFlag } from '@fortawesome/free-solid-svg-icons';

class AsideMenu extends React.Component {
  constructor(props) {
    super();
    this.state = {
      startTime: new Date().getTime(),
      time: '00:00'
    }
  }

  componentDidMount() {



  }

  handleDificult = (event) => {
    event.preventDefault();
    this.props.handleGameStatus({}, false);
  }


  render () {

    setTimeout(() => {
      const newDate = new Date().getTime();
      const time = Math.floor((newDate - this.state.startTime) / 1000);
      const seconds = time % 60;
      const minutes = Math.floor(time / 60);
        this.setState({
          time: (minutes < 10 ? '0' : '') + minutes + ':' + (seconds < 10 ? '0' : '') +seconds
        })
    }, 1000);


    return (
      <div className="aside-menu">
        <div className="menu-header">
          <div className="header-block">
          <FontAwesomeIcon icon={faFlag} style={{fontSize: '30px'}}/>
            <div>00:00</div>
          </div>

          <div className="header-block">
          <FontAwesomeIcon icon={faClock} style={{fontSize: '30px'}}/>
            <div>{this.state.time}</div>
          </div>
        </div>

        <div className="menu-footer">
          <button className="menu-btn" onClick={(e) => {
            e.preventDefault();
            this.props.handleDificult({
              width: this.props.gameDifficult.width,
              height: this.props.gameDifficult.height
            });
          }}>Начать сначала</button>
          <button className="menu-btn menu-btn-active" onClick={this.handleDificult}>Изменить сложность</button>
          <button className="menu-btn">Пауза</button>
        </div>

      </div>
    );
  }
}

export default AsideMenu;
