import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVirus } from '@fortawesome/free-solid-svg-icons';


class Ceil extends React.Component {
  constructor(props) {
    super();

    this.bgColors = ['#f4f4f4', '#D7F7CA', '#DCDA8F', '#EEC58F', '#FFA166', '#FF7566', '#FF5166'];

    this.state = {
      opened: props.ceilOptions.opened,
      mined: props.ceilOptions.mined,
      backgroundColor: this.props.ceilOptions.mined ? '#666' :
        this.props.ceilOptions.opened ? this.bgColors[this.props.ceilOptions.minesAround] : '#BABDB6'
    }
  }

  handleClick = (event) => {
    if (this.state.opened) {
      return;
    } 

    this.props.handleClick();
  }

  render() {

    const fz = this.props.styleOptions.width < 40 ? '20px' : this.props.styleOptions.width < 50 ? '30px' : '50px';

    const ceilStyle = {
      ...this.props.styleOptions,
      backgroundColor: this.state.backgroundColor
    };

    const textContent = 
    this.props.ceilOptions.minesAround > 0 ?
    this.props.ceilOptions.minesAround :
    this.props.ceilOptions.mined ? 
    (<FontAwesomeIcon icon={faVirus} style={{fontSize: `${this.props.styleOptions.width}`, textAlign: 'center', width: '100%'}}/>) :
    '';

    return (
      <div className='ceil' style={ceilStyle} onClick={this.handleClick}>
        <div className='ceil-content' style={{fontSize: `${fz}`}}>
          {this.state.opened ? textContent : null}
        </div>
      </div>
    );
  }
}

export default Ceil;
