import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVirus, faFlag } from '@fortawesome/free-solid-svg-icons';


class Cell extends React.Component {
  constructor(props) {
    super();
    this.bgColors = ['#E9EAE9', '#DDFAC3', '#ECEDBF', '#EDDAB4', '#FFA166', '#FF7566', '#FF5166'];
  }

  handleClick = (event) => {
    event.preventDefault();

    if (this.props.cellOptions.opened) {
      return;
    } 

    if (this.props.isGameOver) {
      return;
    }

    if (event.type === 'click') {
      this.props.handleClick(this.props.cellOptions.id, 'click');
    } else if (event.type === 'contextmenu') {
      this.props.handleClick(this.props.cellOptions.id, 'contextmenu');
    }
  }

  render() {


    const fz = this.props.styleOptions.width < 40 ? '20px' : this.props.styleOptions.width < 50 ? '30px' : '50px';

    const cellStyle = {
      ...this.props.styleOptions,

      backgroundColor: 
        //Cheat mode settings
        this.props.cheatOn ?
         this.props.cellOptions.mined ?
          'red' :
          this.props.cellOptions.opened ?
          this.bgColors[this.props.cellOptions.minesAround] :
           '#BABDB6' :
        //Default settings
        this.props.cellOptions.mined && this.props.cellOptions.opened ?
         this.props.cellOptions.loss ?
          'red' :
           '#666' :
        this.props.cellOptions.opened ?
         this.bgColors[this.props.cellOptions.minesAround] :
          '#BABDB6'
    };

    const textContent = this.props.cellOptions.minesAround > 0 ?
    this.props.cellOptions.minesAround :
    this.props.cellOptions.mined ? 
    (<FontAwesomeIcon icon={faVirus} style={{fontSize: `${this.props.styleOptions.width}`, textAlign: 'center', width: '100%'}}/>) :
    '';

    return (
      <div className='cell' style={cellStyle}
        onClick={this.handleClick}
        onContextMenu={this.handleClick}
      >
        <div className='cell-content' style={{fontSize: `${fz}`}}>
          {this.props.cellOptions.opened ? textContent :
           this.props.cellOptions.flagged ? (<FontAwesomeIcon icon={faFlag} style={{fontSize: `${this.props.styleOptions.width}`, textAlign: 'center', width: '100%'}} />) : null}
        </div>
      </div>
    );
  }
}

export default Cell;
