import React from 'react';
import Ceil from './ceil';

class Board extends React.Component {
  constructor(props) {
    super();
  }

  state = {
    boardWidth: 0,
    ceilSize: 0
  }

  componentDidMount() {
    if (this.state.boardWidth === 0) {
      this.setBoardWidth();
    }
  }

  setBoardWidth = () => {
    let newCeilSize = 60;

    if (this.props.gameDifficult.width === '16') {
      newCeilSize = 30;
    }  else if (this.props.gameDifficult.width === '30') {
      newCeilSize = 30;
    }

    let newBoardWidth = +this.props.gameDifficult.width * newCeilSize + (this.props.gameDifficult.width * 2);

    this.setState({
      boardWidth: newBoardWidth,
      ceilSize: newCeilSize
    })
  }

  getCeil = () => {
    let ceilArray = [];
    let mines = 0;
    if (this.props.gameDifficult.width === '8') {
      mines = 10;
    } else if (this.props.gameDifficult.width === '16') {
      mines = 40;
    } else if (this.props.gameDifficult.width === '30') {
      mines = 99;
    }

    let minesArray = [];

    for (let i = 1; i <= mines; i++) {
      let randomWidth = Math.floor(Math.random() * this.props.gameDifficult.width + 1);
      let randomHeight = Math.floor(Math.random() * this.props.gameDifficult.height + 1);
      minesArray.push([randomWidth, randomHeight]);
    }

    console.log(minesArray);

    for (let i = 1; i <= this.props.gameDifficult.height; i++) {
      for (let j = 1; j <= this.props.gameDifficult.width; j++) {
        ceilArray.push(
          <Ceil 
            key={Math.random()  * 1000 + j * i}
            ceilCords={{width: j, heigth: i}}
            styleOptions={{width: this.state.ceilSize, height: this.state.ceilSize}}
          />
        );
      }
    }

    return ceilArray;
  }

  render () {

    // console.log(this.state);
    return (
      <div className='board' style={{width: `${this.state.boardWidth}px`}}>

        {this.getCeil()}

      </div>
    );
  }
}

export default Board;
