import React from 'react';
import Ceil from './ceil';

class Board extends React.Component {
  constructor(props) {
    super();
  }

  getCeil = () => {
    let ceilArray = [];

    for (let key of Object.keys(this.props.gameState.ceils)) {

      ceilArray.push(
        <Ceil 
          key={key}
          ceilOptions={this.props.gameState.ceils[key]}
          styleOptions={{width: this.props.gameState.ceilSize, height: this.props.gameState.ceilSize}}
        />
      );
    }

    return ceilArray;
  }

  render () {
    return (
      <div className='board' style={{width: this.props.gameState.boardWidth}}>

        {this.getCeil()}

      </div>
    );
  }
}

export default Board;
