import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVirus } from '@fortawesome/free-solid-svg-icons';


class Ceil extends React.Component {
  constructor(props) {
    super();
  }

  render() {



    const fz = this.props.styleOptions.width < 40 ? '20px' : this.props.styleOptions.width < 50 ? '30px' : '50px';
    let backgroundColor = '#BABDB6';

    if (this.props.ceilOptions.minesAround === 1) {
      backgroundColor = '#D7F7CA';
    } else if (this.props.ceilOptions.minesAround === 2) {
      backgroundColor = '#DCDA8F';
    } else if (this.props.ceilOptions.minesAround === 3) {
      backgroundColor = '#EEC58F';
    } else if (this.props.ceilOptions.minesAround === 4) {
      backgroundColor = '#FFA166';
    } else if (this.props.ceilOptions.minesAround === 5) {
      backgroundColor = '#FF7566';
    }

    const style = {
      ...this.props.styleOptions,
      backgroundColor: this.props.ceilOptions.mined ? '#666' : backgroundColor
    };

    const textContent = 
    this.props.ceilOptions.minesAround > 0 ?
    this.props.ceilOptions.minesAround :
    this.props.ceilOptions.mined ? 
    (<FontAwesomeIcon icon={faVirus} style={{fontSize: '30px'}}/>) :
    '';
    console.log(textContent);

    return (
      <div className='ceil' style={style}>
        <div className='ceil-content' style={{fontSize: `${fz}`}}>
          {
            textContent
          }
        </div>
      </div>
    );
  }
}

export default Ceil;
