import React from 'react';
import StartMenu from './start-menu';
import Game from './game';
import './main.css';
class App extends React.Component {

  constructor() {
    super();
  }

  state = {
    isStartGame: true
  }

  render() {
    return (
      <div className="App">
        <div className="container">

          {this.state.isStartGame ? <Game/> :  <StartMenu />}

        </div>
      </div>
    );
  }
}

export default App;
