import React from 'react';
import StartMenu from './start-menu';
import Game from './game';
import './main.css';


class App extends React.Component {

  constructor() {
    super();
  }

  state = {
    gameDificult: {
      width: '',
      height: ''
    },
    isStartGame: false
  }

  handleGameStatus = (gameDificultProps, init) => {
    if (init === false) {
      this.state({
        gameDificult: {
          width: '',
          height: ''
        },
        isStartGame: false
      });
    } else {
      this.setState({
        gameDificult: {
          width: gameDificultProps.width,
          height: gameDificultProps.height
        },
        isStartGame: true
      })
    }
  }

  render() {
    console.log(this.state);

    return (
      <div className="App">

          {this.state.isStartGame ?
            <Game handleGameStatus={this.handleGameStatus}/> :
            <StartMenu handleGameStatus={this.handleGameStatus} />}   

      </div>
    );
  }
}

export default App;
